          "sku": "{{../product.sku}}",
          "brand": {
            "@type": "Thing",
            "name": "{{../product.brand.name}}"
          },
          "aggregateRating": {
            "@type": "AggregateRating",
            "ratingValue": "{{rating}}",
            "reviewCount": "{{../product.num_reviews}}"
          },
          "review": {
            "author": {
              "@type": "Organization",
              "name": "{{../settings.store_name}}"
            },
            "name": "{{product.title}}",
            "reviewRating": {
              "@type": "Rating",
              "ratingValue": "{{rating}}"
            }
          }
        },
        "reviewRating": {
          "@type": "Rating",
          "ratingValue": "{{rating}}"
        },
        "name": "{{title}}",
        "author": {
          "@type": "Person",
          "name": "{{name}}"
        },
        "reviewBody": "{{text}}",
        "publisher": {
          "@type": "Organization",
          "name": "{{../settings.store_name}}"
